<?php	$wiki = $vars['entity'];	$icon = elgg_view_entity_icon($wiki, 'medium', array('href'=>$wiki->thumbnail));		$metadata = elgg_view_menu('entity', array(		'entity' => $wiki,		'handler' => 'wiki',		'sort_by' => 'priority',		'class' => 'elgg-menu-hz',	));		$params = array(		'entity' => $wiki,		'metadata' => $metadata,		'content' => $wiki->description	);		$list_body = elgg_view('object/elements/summary', $params);		//we need to merge these items into the metadata above!    $site = get_entity($wiki->site_guid); 	         echo elgg_view_image_block($icon, $list_body);?>

