<?php	$wiki = $vars['entity'];		$icon = elgg_view_entity_icon($wiki, 'small');		$metadata = elgg_view_menu('entity', array(		'entity' => $wiki,		'handler' => 'wiki',		'sort_by' => 'priority',		'class' => 'elgg-menu-hz',	));		$params = array(		'entity' => $wiki,		'metadata' => $metadata,		'subtitle' => $wiki->description	);		$list_body = elgg_view('object/elements/summary', $params);	echo elgg_view_image_block($icon, $list_body);		    ?>