<?php	$icon = elgg_view_entity_icon($vars['entity'], 'medium', array('href'=>$vars['entity']->thumbnail));		$metadata = elgg_view_menu('entity', array(		'entity' => $vars['entity'],		'handler' => 'groups',		'sort_by' => 'priority',		'class' => 'elgg-menu-hz',	));		$params = array(		'entity' => $vars['entity'],		'metadata' => $metadata,		'content' => $vars['entity']->description	);	$list_body = elgg_view('object/elements/summary', $params);		//we need to merge these items into the metadata above!    $site = get_entity($vars['entity']->site_guid); 	$list_body .= elgg_view('output/url', array('text' => 'visit this wiki', 'href'=>$vars['entity']->url))." "			.elgg_view('output/url', array('text' => 'permalink', 'href'=>$vars['entity']->getURL()))." "    .elgg_view('output/url', array('text' => 'manage this wiki', 'href'=>$site->url."socialwiki/wikis/manage/".$vars['entity']->guid))." "     .elgg_view('output/url', array('text' => 'social view', 'href'=>$site->url."socialwiki/wikis/".($vars['entity']->guid)."/view/ЧсенЭЩ ЧсбЦэгэЩ", 'encode_text'=>true))." "    .elgg_view('output/url', array('text' => 'recent changes', 'href'=>$site->url."socialwiki/wikis/".($vars['entity']->guid)."/recentchanges", 'encode_text'=>true));        echo elgg_view_image_block($icon, $list_body);    ?>