<?php	$wiki = $vars['entity'];	$icon = elgg_view_entity_icon($wiki, 'medium', array('href'=>$wiki->thumbnail));		$metadata = elgg_view_menu('entity', array(		'entity' => $wiki,		'handler' => 'groups',		'sort_by' => 'priority',		'class' => 'elgg-menu-hz',	));		$params = array(		'entity' => $wiki,		'metadata' => $metadata,		'content' => $wiki->description	);		$list_body = elgg_view('object/elements/summary', $params);		//we need to merge these items into the metadata above!    $site = get_entity($wiki->site_guid); 	$list_body .= elgg_view('output/url', array('text' => 'visit this wiki', 'href'=>$wiki->url))." "			.elgg_view('output/url', array('text' => 'permalink', 'href'=>$wiki->getURL()))." "    .elgg_view('output/url', array('text' => 'manage this wiki', 'href'=>$site->url."wiki/manage/".$wiki->guid))." "     .elgg_view('output/url', array('text' => 'social view', 'href'=>$site->url."wiki/view/".($wiki->guid)."/الصفحة الرئيسية", 'encode_text'=>true))." "    .elgg_view('output/url', array('text' => 'recent changes', 'href'=>$site->url."wiki/recentchanges/".($wiki->guid), 'encode_text'=>true));        echo elgg_view_image_block($icon, $list_body);?>
